Bash(SOLANA_RPC_URL="https://mainnet.helius-rpc.com/?api-key=24396234-4302-44e5-a4ed-ee5bfe6d5c55" node -e "
const walletAddress = ''AnQoSxGdSd7q48b75ygSwNpM4VdN5fdJNVxtPQKJHfCe'';
const apiKey = ''24396234-4302-44e5-a4ed-ee5bfe6d5c55'';
const SOL_MINT = ''So11111111111111111111111111111111111111112'';

async function main\(\) {
  const url = ''https://api.helius.xyz/v0/addresses/'' + walletAddress + ''/transactions?api-key='' + apiKey;
  const res = await fetch\(url\);
  const txs = await res.json\(\);

  console.log\(''Analyzing'', txs.length, ''transactions...''\);

  for \(const tx of txs\) {
    if \(tx.type !== ''SWAP'' && tx.type !== ''SWAP_WSOL''\) continue;

    const tokenTransfers = tx.tokenTransfers || [];
    const nativeTransfers = tx.nativeTransfers || [];

    // Calculate net flows
    const tokenFlows = new Map\(\);
    for \(const transfer of tokenTransfers\) {
      if \(transfer.mint === SOL_MINT\) continue;
      if \(!tokenFlows.has\(transfer.mint\)\) {
        tokenFlows.set\(transfer.mint, { in: 0, out: 0, decimals: transfer.decimals || 6 }\);
      }
      const flow = tokenFlows.get\(transfer.mint\);
      if \(transfer.toUserAccount === walletAddress\) flow.in += transfer.tokenAmount || 0;
      if \(transfer.fromUserAccount === walletAddress\) flow.out += transfer.tokenAmount || 0;
    }

    let solIn = 0, solOut = 0;
    for \(const transfer of nativeTransfers\) {
      if \(transfer.toUserAccount === walletAddress\) solIn += transfer.amount || 0;
      if \(transfer.fromUserAccount === walletAddress\) solOut += transfer.amount || 0;
    }
    const netSol = solIn - solOut;

    // Find main token
    let mainToken = null, mainTokenNet = 0;
    for \(const [mint, flow] of tokenFlows\) {
      const net = flow.in - flow.out;
      if \(Math.abs\(net\) > Math.abs\(mainTokenNet\)\) {
        mainTokenNet = net;
        mainToken = { mint: mint.slice\(0, 8\), net, decimals: flow.decimals };
      }
    }

    console.log\(''---''\);
    console.log\(''Sig:'', tx.signature.slice\(0, 20\) + ''...''\);
    console.log\(''Net SOL:'', \(netSol / 1e9\).toFixed\(6\), ''SOL''\);
    console.log\(''Main token:'', mainToken ? mainToken.mint + '': '' + mainTokenNet.toFixed\(2\) : ''NONE''\);

    // Check conditions
    if \(!mainToken || Math.abs\(mainTokenNet\) < 1\) {
      console.log\(''SKIP: no significant token movement''\);
      continue;
    }
    if \(Math.abs\(netSol\) < 1000\) {
      console.log\(''SKIP: no significant SOL movement \(netSol='' + netSol + ''\)''\);
      continue;
    }

    if \(mainTokenNet > 0 && netSol < 0\) console.log\(''=> BUY''\);
    else if \(mainTokenNet < 0 && netSol > 0\) console.log\(''=> SELL''\);
    else console.log\(''SKIP: unclear direction \(tokenNet='' + mainTokenNet + '', solNet='' + netSol + ''\)''\);
  }
}
main\(\);
")

